    /* ~~~~~~~~~~~~~~~~~~~ Methods for parsing Commands ~~~~~~~~~~~~~~~~~~~~~~*/

    public static String getOutput(String serverResponse, int numMessages) {

        // Checking for the command operator and making sure that the bot doesn't respond to itself
        if (serverResponse.contains("/") && serverResponse.contains("<") && serverResponse.contains(">")) {

            if (serverResponse.contains("/help")) {

                // Calling the helpCommand() method
                return helpCommand();

            } else if (serverResponse.contains("/get_num_messages")) {

                // Calling the dodCommand() method
                return numMessagesCommand(numMessages);

            } else if (serverResponse.contains("/overlord")) {

                // Calling the overlordCommand() method
                return overlordCommand();

            } else if (serverResponse.contains("/joke")) {

                // Calling the jokeCommand() method
                return jokeCommand();

            } else {

                // Command not recognised
                return "Unknown command, please try again";

            }

        }

        // If the command operator is not detected, simply return an empty string
        return "";

    }

    /* ~~~~~~~~~~~~~~~~~~~ Methods for running Commands ~~~~~~~~~~~~~~~~~~~~~~*/

    private static String helpCommand() {

        return "-----------------------------------------------------\n" +
                "<Elon (Bot)> help - Returns a list of available commands\n" +
                "<Elon (Bot)> get_num_messages - Returns the number of messages sent by all users since the ChatBot started\n" +
                "<Elon (Bot)> overlord - Reveals the lord Elon himself\n" +
                "<Elon (Bot)> -----------------------------------------------------";

    }

    private static String numMessagesCommand(int numMessages) {

        return "Y'all have sent a measly " + numMessages + " messages. Smh my head, Elon sends that in a minute";

    }

